// parseAwards: возвращает массив объектов {type: 'oscar'|'emmy'|'awards'|'golden'|'bafta', count: N}
function parseAwards(awardsText, localCurrentCard) {
    if (typeof awardsText !== 'string') return [];
    var result = [];
    // Oscars
    var oscarMatch = awardsText.match(/Won (\d+) Oscars?/i);
    if (oscarMatch && oscarMatch[1]) {
        result.push({ type: 'oscars', count: parseInt(oscarMatch[1], 10) });
    }
    // Primetime Emmys
    var emmyMatch = awardsText.match(/Won (\d+) Primetime Emmys?/i);
    if (emmyMatch && emmyMatch[1]) {
        result.push({ type: 'emmy', count: parseInt(emmyMatch[1], 10) });
    }
    // Golden Globes (common phrasing)
    var goldenMatch = awardsText.match(/Won (\d+) Golden Globes?/i);
    if (goldenMatch && goldenMatch[1]) {
        result.push({ type: 'golden', count: parseInt(goldenMatch[1], 10) });
    }
    // BAFTA
    var baftaMatch = awardsText.match(/Won (\d+) BAFTAs?/i);
    if (baftaMatch && baftaMatch[1]) {
        result.push({ type: 'bafta', count: parseInt(baftaMatch[1], 10) });
    }
    // Other wins (Another N wins)
    var otherMatch = awardsText.match(/Another (\d+) wins?/i);
    if (otherMatch && otherMatch[1]) {
        result.push({ type: 'awards', count: parseInt(otherMatch[1], 10) });
    }
    // Generic wins: 'X wins' (fallback)
    var simpleMatch = awardsText.match(/(\d+) wins?/i);
    if (simpleMatch && simpleMatch[1]) {
        // if not already included as 'awards' or similar, add
        var n = parseInt(simpleMatch[1], 10);
        var exists = result.some(function(r){ return r.count === n; });
        if (!exists) result.push({ type: 'awards', count: n });
    }
    return result;
}



function insertAwards(awards) {
    var render = Lampa.Activity.active().activity.render();
    if (!render) return;
    var rateLine = $('.full-start-new__rate-line', render);
    if (!rateLine.length) return;
    // iterate awards array and insert elements in front if not exist
    awards.forEach(function(a) {
        if (!a || !a.type || !a.count) return;
        var className = 'rate--' + (a.type === 'oscars' ? 'oscars' : a.type);
        if ($('.' + className, rateLine).length) return;
        var icon = AWARD_ICONS[a.type] || (ICON_IMDB || '') ; // fallback
        var elemHtml = '<div class="full-start__rate ' + className + ' rate--award">' +
                        '<div>' + a.count + '</div>' +
                        '<div class="source--name">' + icon + '</div>' +
                       '</div>';
        // prepend so awards show first
        rateLine.prepend(elemHtml);
    });
}


