var awardsStyle = "<style id=\"maxsm_omdb_awards\">" +
".rate--award { display:flex; align-items:center; color:gold; gap:0.3em; }" +
".rate--award svg, .rate--award img { height:14px; width:auto; vertical-align:middle; }" +
"</style>";
Lampa.Template.add('awards_css', awardsStyle);
$('body').append(Lampa.Template.get('awards_css', {}, true));

function parseAwards(awardsText) {
    if (typeof awardsText !== 'string') return [];
    var result = [];
    var m;
    m = awardsText.match(/Won (\d+) Oscars?/i); if (m) result.push({type:'oscars', count: parseInt(m[1],10)});
    m = awardsText.match(/Won (\d+) Primetime Emmys?/i); if (m) result.push({type:'emmy', count: parseInt(m[1],10)});
    m = awardsText.match(/Won (\d+) Golden Globes?/i); if (m) result.push({type:'golden', count: parseInt(m[1],10)});
    m = awardsText.match(/Won (\d+) BAFTAs?/i); if (m) result.push({type:'bafta', count: parseInt(m[1],10)});
    m = awardsText.match(/Another (\d+) wins?/i); if (m) result.push({type:'awards', count: parseInt(m[1],10)});
    if (!result.length) { m = awardsText.match(/(\d+) wins?/i); if (m) result.push({type:'awards', count: parseInt(m[1],10)}); }
    return result;
}

function insertAwards(awards) {
    var render = Lampa.Activity.active().activity.render();
    if (!render) return;
    var rateLine = $('.full-start-new__rate-line', render);
    if (!rateLine.length) return;
    awards.forEach(function(a) {
        if (!a || !a.type) return;
        var cls = 'rate--' + (a.type === 'oscars' ? 'oscars' : a.type);
        if ($('.' + cls, rateLine).length) return;
        var icon = AWARD_ICONS[a.type] || '';
        var html = '<div class="full-start__rate ' + cls + ' rate--award">' +
                   '<div>' + (a.count || '') + '</div>' +
                   '<div class="source--name">' + icon + '</div>' +
                   '</div>';
        rateLine.prepend(html);
    });
}

